Example:
Test reset method.$(DDOX_UNITTEST_HEADER __unittest_L744_C1)
---
import quark.common.utils : fequals;

auto transform = new TransformComponent();
transform.position = Vec2f(10.0f, 20.0f);
transform.rotation = PI;
transform.scale = Vec2f(2.0f, 3.0f);
transform.size = Vec2f(64.0f, 48.0f);

transform.reset();

assert(transform.position.x.fequals(0.0f) && transform.position.y.fequals(0.0f),
    "Position should be reset to (0, 0)");
assert(transform.rotation.fequals(0.0f), "Rotation should be reset to 0");
assert(transform.scale.x.fequals(1.0f) && transform.scale.y.fequals(1.0f),
    "Scale should be reset to (1, 1)");
assert(transform.size.x.fequals(32.0f) && transform.size.y.fequals(32.0f),
    "Size should be reset to (32, 32)");

